			"startingEquipment":{"additionalFromBackground":true, "default":["(a) a {@item rapier|phb}, (b) a {@item longsword|phb}, or (c) any {@filter simple weapon|items|source=phb|category=basic|type=simple weapon}", "(a) a {@item diplomat's pack|phb} or (b) an {@item entertainer's pack|phb}", "(a) a {@item lute|phb} or (b) any other {@filter musical instrument|items|miscellaneous=mundane|type=instrument}", "{@item Leather armor|phb}, and a {@item dagger|phb}"], "goldAlternative":"{@dice 5d4×10|5d4 × 10|Starting Gold}"},
