			"startingEquipment":{"additionalFromBackground":true, "default":["(a) a {@item shortsword|phb} or (b) any {@filter simple weapon|items|source=phb|category=basic|type=simple weapon}", "(a) a {@item dungeoneer's pack|phb} or (b) an {@item explorer's pack|phb}", "10 {@item dart|phb|darts}"], "goldAlternative":"{@dice 5d4|5d4|Starting Gold}"},
