			"startingEquipment":{"additionalFromBackground":true, "default":["(a) a {@item light crossbow|phb} and {@item crossbow bolts (20)|phb|20 bolts} or (b) any {@filter simple weapon|items|source=phb|category=basic|type=simple weapon}", "(a) a {@item component pouch|phb} or (b) an {@item arcane focus|phb}", "(a) a {@item dungeoneer's pack|phb} or (b) an {@item explorer's pack|phb}", "Two {@item dagger|phb|daggers}"], "goldAlternative":"{@dice 3d4×10|3d4 × 10|Starting Gold}"},
